linux.ansible:
---
- name: CM-8 System Component Inventory
  hosts: linux

  tasks:
    - name: Gather comprehensive facts
      setup:
        gather_subset: all

    - name: Create system inventory
      copy:
        content: |
          System Inventory Report - {{ ansible_date_time.iso8601 }}

          Hostname: {{ ansible_hostname }}
          OS: {{ ansible_os_family }}
          OS Version: {{ ansible_distribution_version }}
          Kernel: {{ ansible_kernel }}
          Kernel Version: {{ ansible_kernel_version[0] }}

          Hardware:
          - CPU Cores: {{ ansible_processor_vcpus }}
          - Memory: {{ ansible_memtotal_mb }} MB
          - Disk Devices: {{ ansible_devices | length }}

          Services:
          - Total Packages: {{ ansible_facts.packages | length }}
          - Running Services: {{ ansible_facts.services | length }}

          Network:
          - Interfaces: {{ ansible_interfaces | length }}
          - Default IPv4: {{ ansible_default_ipv4.address | default('N/A') }}
        dest: /var/lib/cm8_system_inventory.txt
        owner: root
        group: root
        mode: '0644'

    - name: Export detailed inventory
      copy:
        content: "{{ ansible_facts | to_nice_json }}"
        dest: /var/lib/cm8_system_inventory.json
        owner: root
        group: root
        mode: '0644'

    - name: Report inventory creation
      debug:
        msg: "System inventory documented"

