windows.powershell:
# SC-42: Sensor Capability and Data - Windows Sensor Audit
# This script audits and manages sensor devices on Windows systems

#Requires -RunAsAdministrator

Write-Host "[SC-42] Auditing sensor devices and capabilities..." -ForegroundColor Cyan

# Audit camera devices
Write-Host "`n=== Camera Devices ===" -ForegroundColor Yellow
$cameras = Get-PnpDevice -Class Camera -ErrorAction SilentlyContinue
if ($cameras) {
    $cameras | Format-Table FriendlyName, Status, InstanceId -AutoSize
} else {
    Write-Host "No camera devices found"
}

# Audit audio input devices (microphones)
Write-Host "`n=== Audio Input Devices ===" -ForegroundColor Yellow
$audioDevices = Get-PnpDevice -Class AudioEndpoint -ErrorAction SilentlyContinue | 
    Where-Object { $_.FriendlyName -like "*Microphone*" -or $_.FriendlyName -like "*Input*" }
if ($audioDevices) {
    $audioDevices | Format-Table FriendlyName, Status -AutoSize
} else {
    Write-Host "No microphone devices found"
}

# Audit sensor devices
Write-Host "`n=== Sensor Devices ===" -ForegroundColor Yellow
$sensors = Get-PnpDevice -Class Sensor -ErrorAction SilentlyContinue
if ($sensors) {
    $sensors | Format-Table FriendlyName, Status -AutoSize
} else {
    Write-Host "No sensor devices found"
}

# Check camera privacy settings
Write-Host "`n=== Camera Privacy Settings ===" -ForegroundColor Yellow
$cameraPrivacy = Get-ItemProperty -Path "HKLM:\SOFTWARE\Microsoft\Windows\CurrentVersion\CapabilityAccessManager\ConsentStore\webcam" -ErrorAction SilentlyContinue
if ($cameraPrivacy) {
    Write-Host "Camera access consent: $($cameraPrivacy.Value)"
} else {
    Write-Host "Camera privacy settings not configured"
}

# Check microphone privacy settings
Write-Host "`n=== Microphone Privacy Settings ===" -ForegroundColor Yellow
$micPrivacy = Get-ItemProperty -Path "HKLM:\SOFTWARE\Microsoft\Windows\CurrentVersion\CapabilityAccessManager\ConsentStore\microphone" -ErrorAction SilentlyContinue
if ($micPrivacy) {
    Write-Host "Microphone access consent: $($micPrivacy.Value)"
}

# Function to disable a device
function Disable-SensorDevice {
    param([string]$DeviceClass, [string]$DeviceName)
    $device = Get-PnpDevice -Class $DeviceClass | Where-Object { $_.FriendlyName -like "*$DeviceName*" }
    if ($device) {
        $device | Disable-PnpDevice -Confirm:$false
        Write-Host "Disabled: $($device.FriendlyName)" -ForegroundColor Green
    }
}

# Uncomment to disable cameras:
# Get-PnpDevice -Class Camera | Disable-PnpDevice -Confirm:$false

Write-Host "`n[SC-42] Sensor audit complete" -ForegroundColor Cyan
